import React, { Component } from 'react';
import YTsearch from 'youtube-api-search';
import SearchBar from './components/SearchBar';
import MainVideo from './components/MainVideo';
import ListOfRelatedVideos from './components/ListOfRelatedVideos'

const authKey = '-MYnXlXjxodQ';

class App extends Component {
  state = { 
    videos: [],
    selectedVideo: null,
  };

  handleSearch = searchText => {
    YTsearch({key: authKey, term: searchText}, (data) => {
      console.log(data);
      this.setState({ videos: data, selectedVideo: data[0] })
    });
  }

  render() { 
    this.handleSearch('react')
    return ( 
      <div>
        <SearchBar handleText={searchText => {this.handleSearch(searchText)}}/>
        <MainVideo video={this.state.selectedVideo}/>
        <ListOfRelatedVideos 
          handleSelect={selected => {this.setState({selectedVideo: selected})}}
          videos={this.state.videos}
        />
      </div>
     );
  }

}

export default App;
