import React, { Component } from 'react';
import YTsearch from 'youtube-api-search';
import Header from './components/Header';
import SearchBar from './components/SearchBar';
import MainVideo from './components/MainVideo';
import ListOfRelatedVideos from './components/ListOfRelatedVideos'
import WatchHistory from './components/WatchHistroy'

const authKey = 'AIzaSyA5S-skHku6RjMDIue2pE78FTd6cN13OvA';

class App extends Component {
  state = { 
    text: '',
    videos: [],
    selectedVideo: null,
    watchedVideos: []
  };

  componentDidMount() {
    this.handleSearch('web development')
    console.log("This project is done by Ahmad Al-Ghzawi.");
  }

  handleSearch = searchText => {
    YTsearch({key: authKey, term: searchText, esults: 20}, (data) => {
      const watchedVideos = this.state.watchedVideos;
      watchedVideos.push(data[0])
      this.setState({ videos: data, selectedVideo: data[0], watchedVideos })
    });
  }

  handleText = text => {
    this.setState({text})
    this.handleSearch(text)
  }

  handleSelect = selectedVideo => {
    const watchedVideos = this.state.watchedVideos;
    watchedVideos.push(selectedVideo)
    this.setState({selectedVideo, watchedVideos})
  }

  render() { 
    return ( 
      <div className = 'container-fluid'>
        <Header />
        <SearchBar
          text={this.state.text}
          handleText={this.handleText} 
          handleClick={this.handleClick}
        />
        <div className = 'row'>
          <WatchHistory 
            videos={this.state.watchedVideos}
            handleSelect={this.handleSelect}
          />
          <MainVideo 
            video={this.state.selectedVideo}
          />
          <ListOfRelatedVideos 
            videos={this.state.videos}
            handleSelect={this.handleSelect}
          />
        </div>
      </div>
     );
  }

}

export default App;
