import React, { Component } from 'react';
import ListItem from './ListItem';

class ListOfRelatedVideos extends Component {
    render(props) {
        let items = this.props.videos.map(video => 
            <ListItem 
                selectedVideo={this.props.selectedVideo}
                handleSelect={this.props.handleSelect}
                key={video.etag}
                video={video}
            />
        )

        let randRelated = new Array(4);
        randRelated = randRelated.forEach(index => index = Math.floor(Math.random() * 5));
        console.log(randRelated);
        items = items.filter( item => item[[...randRelated]] );

        return (

            <div 
                style = {{ maxHeight: 700, overflow: "auto"}}
                className = 'col-md-3'
            >
                <h4>Realated Videos</h4>
                {items}
            </div>
        )
    }
}

export default ListOfRelatedVideos;
