import React, { Component } from 'react';
import YTsearch from 'youtube-api-search';
import SearchBar from './components/SearchBar';
import MainVideo from './components/MainVideo';
import ListOfVideos from './components/ListOfVideos'

const authKey = 'AIzaSyCIEJt6nFAASpAyKvePHy7vjYY0tXpBS38';

class App extends Component {
  state = { 
    videos: [],
    selectedVideo: null,
   };

   handleSearch = searchText => {
    YTsearch({key: authKey, term: searchText}, (data) => {
      console.log(data);
      this.setState({ videos: data, slsectedVideo: data[0] })
    });
   }

  render() { 
    return ( 
      <div>
        <SearchBar searchTextChange={ searchText => this.handleSearch(searchText)}/>
        <MainVideo video={this.state.selectedVideo}/>
        <ListOfVideos 
          selectVideo={selected => this.setState({selectVideo: selected})}
          videos={this.state.videos}
        />
      </div>
     );
  }
}

export default App;
