import React, { Component } from 'react';
import YTsearch from 'youtube-api-search';
import SearchBar from './components/SearchBar';
import MainVideo from './components/MainVideo';
import ListOfVideos from './components/ListOfRelatedVideos'

const authKey = 'AIzaSyCIEJt6nFAASpAyKvePHy7vjYY0tXpBS38';

class App extends Component {
  state = { 
    text: '',
    videos: [],
    selectedVideo: null,
  };

  handleSearch = searchText => {
    YTsearch({key: authKey, term: searchText}, (data) => {
      console.log(data);
      this.setState({ videos: data, selectedVideo: data[0] })
    });
  }

  handleText = event => {
    this.setState({text: event.target.value});
    this.handleSearch(this.state.text)
  }

  render() { 
    return ( 
      <div>
        <SearchBar handleText={this.handleText} text={this.state.text}/>
        <MainVideo video={this.state.selectedVideo}/>
        <ListOfVideos 
          selectVideo={selected => this.setState({selectedVideo: selected})}
          videos={this.state.videos}
        />
      </div>
     );
  }

}

export default App;
