import React, { Component } from 'react';

class ListItem extends Component {

    render(props) { 
        return ( 
            <div className = {this.props.selectedVideo ? "" : "col-md-4"} >
                <div
                    className = "card mb-3 related-videos border-0" 
                    style={this.props.selectedVideo ? {} : {height: 150, overflow: "hidden"}}  
                    onClick={ () => this.props.handleSelect(this.props.video) }

                >
                    <div className = "row no-gutters">
                        <div 
                            className = "col-md-4"   
                        >
                            <img 
                                style={{maxWidth: 200, maxHeight: 250}} 
                                className = "card-img"
                                src={this.props.video.snippet.thumbnails.default.url}
                                alt={this.props.video.snippet.title}    
                            />
                        </div>
                        <div className="col-md-8 d-flex align-items-start">
                            <div className="Card-body ml-2">
                                <p className = "card-title font-weight-bold">{this.props.video.snippet.title}</p>
                                <p className = "card-text">{this.props.selectedVideo ? "" : this.props.video.snippet.description}</p>
                            </div>
                        </div>
                    </div>
                </div>
                <hr />
            </div>
         );
    }

}

export default ListItem;
