import React, { Component } from 'react';

class ListItem extends Component {
    render(props) { 
        return ( 
            <div
                className = "card mb-3 related-videos" 
                onClick={ () => this.props.handleSelect(this.props.video) }

            >
                <div className = "row no-gutters">
                    <div className = "col-md-4">
                        <img 
                            className = "card-img"
                            style={{maxWidth: 150, maxHeight}}
                            src={this.props.video.snippet.thumbnails.default.url}
                            alt={this.props.video.snippet.title}    
                        />
                    </div>
                    <div className="col-md-8 d-flex align-items-center">
                        <div className="Card-body ml-2">
                            <p className = "card-title">{this.props.video.snippet.title}</p>
                        </div>
                    </div>
                </div>
            </div>
         );
    }
}

export default ListItem;
