import React, { Component } from 'react';
import YTsearch from 'youtube-api-search';
import SearchBar from './components/SearchBar';
import MainVideo from './components/MainVideo';
import ListOfRelatedVideos from './components/ListOfRelatedVideos'

const authKey = 'AIzaSyDlG7YE2BAPx_-AV-0on4DRtxtBeJpdQOs';

class App extends Component {
  state = { 
    text: '',
    videos: [],
    selectedVideo: null,
  };

  componentDidMount() {
    this.handleSearch('web development')
  }

  handleSearch = searchText => {
    YTsearch({key: authKey, term: searchText}, (data) => {
      this.setState({ videos: data, selectedVideo: data[0] })
    });
  }

  handleText = event => {
    if(event.key === 'Enter') {
      const text = event.target.value;
      this.setState({text})
      this.handleSearch(text)
    }
  }

  handleClick = textToSearch => {
    const text = textToSearch;
    this.toProcessText.value = ''
    this.setState({text})
    this.handleSearch(text)
  }

  handleSelect = selectedVideo => {
    this.setState({selectedVideo})
  }

  render() { 
    return ( 
      <div className = 'container-fluid'>
        <SearchBar
          ref
          text={this.state.text}
          handleText={this.handleText} 
          handleClick={this.handleClick}
        />
        <div className = 'row'>
          <div className = 'col-md-3'>
          {/* <Watched /> */}
          </div>
          <MainVideo 
            video={this.state.selectedVideo}
          />
          <ListOfRelatedVideos 
            videos={this.state.videos}
            handleSelect={this.handleSelect}
          />
        </div>
      </div>
     );
  }

}

export default App;
